catalog_script_client.script:
function onChange() {
    //check if related ritm defined in url
    if (g_form.getValue('related_ritm')) {
        g_form.getReference('related_ritm', getVars);
    }
}

//Get variables from original request via Incident To Request Utils script include
function getVars(request) {
    var ga = new GlideAjax('Incident_To_Request_Utils');
    ga.addParam("sysparm_name", "getRequestVars");
    ga.addParam("number", request.sys_id);
    ga.getXMLAnswer(function(response) {
        if (response) {
            response = JSON.parse(response);
            g_form.setDisplay('related_ritm', true);
            var fields = g_form.getFieldNames();
            for (var i = 0; i < fields.length; i++) {
                if (response[fields[i]]) {
					stepThroughValues(fields,response);
                    //g_form.setValue(fields[i], response[fields[i]]);
					setTimeout(ifEmptyStepThroughValues, 2000, fields, response);
                }
            }
        } else {
            g_form.clearValue('related_ritm');
        }
    });
}

function ifEmptyStepThroughValues(fields,response){
            for (var i = 0; i < fields.length; i++) {
                if (response[fields[i]]) {
					if (g_form.getValue(fields[i]) == ''){
					console.log("Value of " + fields[i] + " is " + g_form.getValue(fields[i]));
					setValue(fields[i], response[fields[i]]);
					}
                }
            }
}

function stepThroughValues(fields,response){
            for (var i = 0; i < fields.length; i++) {
                if (response[fields[i]]) {
					setValue(fields[i], response[fields[i]]);
                }
            }
}

function setValue(field,value){
	g_form.setValue(field, value);
}
