sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {
    var linkText = "Click here";
	var glideURL = 'https://' + gs.getProperty('instance_name') +  '.service-now.com/sp?id=sc_cat_item&amp;sys_id=${u_catalog_item.sys_id}&amp;sysparm_category=${u_catalog_item.category.sys_id}&amp;sysparm_variables=%7B%22related_inc%22:%22${u_incident.sys_id}%22%7D';

    template.print('<a href="' + glideURL + '">' + linkText + '</a>');
})(current, template, email, email_action, event);
