sys_ui_action.client_script_v2:
function onClick(g_form) {

	if( g_form.getValue('caller_id') == '' ||
		g_form.getValue('category') == '' ||
		g_form.getValue('subcategory') == '' ||
		g_form.getValue('contact_type') == '' ||
		g_form.getValue('short_description') == ''){
		g_form.addErrorMessage('Missing required field.');
	}
	else{
		var URL = '/catalog_item_list.do?number=' +
			g_form.getValue('number') + "&state=" +
			g_form.getValue('state') + "&short_description=" +
			g_form.getValue('short_description') + "&caller_id=" +
			g_form.getValue('caller_id') + "&requested_for=" + 
			g_user.userID + "&caller_id_display=" +
			g_form.getValue('caller_id.name') + "&requested_for_display="+
			g_user.firstName + " " + g_user.lastName;
		g_modal.showFrame({title: 'Convert Incident to Request', url: URL, size:'lg', height: '25rem',});
	}
}

sys_ui_action.script:
function processRequest() {
	var arrMand = g_form.getMissingFields();
	if(!g_form.modified){
		if (arrMand.length > 0) {
			g_form.addErrorMessage('Missing required field.');
		} else {
			popModal();
		}
	} else {
		g_form.addErrorMessage('Please save the form before converting the incident.');
	}
}

function popModal(){
	var callerId = g_form.getValue("caller_id");
	var openedBy = g_user.getUserID();
	var dialog = new GlideDialogWindow("catalog_item_list", false, 700);
	dialog.setTitle("");
	dialog.removeCloseDecoration();
	dialog.setPreference("number", g_form.getValue("number"));
	dialog.setPreference("short_description", g_form.getValue("short_description"));
	dialog.setPreference("state", g_form.getValue("state"));
	dialog.setPreference("requested_for", callerId.sys_id);
	dialog.setPreference("caller_id", g_form.getValue("caller_id"));
	dialog.setPreference("requested_for_display", callerId.name);
	dialog.setPreference("caller_id_display", openedBy.firstName + ' ' + openedBy.lastName);
	dialog.setWidth(700);
	dialog.render();
}
